import React, {Component} from "react";
import Cell from "./Cell";
import './Board.css';

class Board extends Component {
  state = {
    win: false,
  }

  cellList = [];
  extractedLetters = new Set()

  generateCells = (char) => {
    return [...Array(5)].map((x,i) => {
      let uid = char + (i + 1);
      this.cellList.push(uid);
      return <Cell id={uid} key={uid} />
    });
  }

  extractLetters = () => {
    return this.cellList.forEach((cell)=>{
      this.extractedLetters.add(cell[0]);
    });
