import React, {Component} from 'react'
import "./Cell.css"

class Cell extends Component {
  state = {
    on: true
  }

  sendData = () => {
    this.props.parentCallback("Hey Popsie, How’s it going?");
}

  render() {
    return (
      <td onClick={() => console.log(this.props.coords)} 
          className={this.state.on ? "Cell Cell-on" : "Cell Cell-off" } >{this.props.id}</td>
    )
  }
}


export default Cell
