import React, {Component} from "react";
import Cell from "./Cell";
import './Board.css';

class Board extends Component {
  state = {
    win: false,
  }

  cellList = [];
  extractedLetters = new Set();
  extractedNums = new Set();

  generateCells = (char) => {
    return [...Array(5)].map((x,i) => {
      let uid = char + (i + 1);
      this.cellList.push(uid);
      let coords = this.lightCoordinates(uid);
      return <Cell coordinates={coords} id={uid} key={uid} />
    });
  }

  extractLetters = () => {                                /* THIS FUNCTION EXTRACTS THE LETTERS FROM THE CELLLIST ARRAY */

    this.cellList.forEach((cell) => {                     // Loop through the 'cellList',
      this.extractedLetters.add(cell[0]);                 // & add a letter to the 'extractedLetters' Set,
      this.extractedNums.add(cell[1]);                    // & add a number to the 'extractedNums' Set
    });

    this.extractedLetters = [...this.extractedLetters];   // Convert the 'extractedLetters' Set to an Array
    this.extractedNums = [...this.extractedNums];         // Convert the 'extractedNums' Set to an Array

  }

  lightCoordinates = (uid) => {
    let up;
    for(let i = 0; i < this.extractedLetters.length; i++){
      if(uid[0] === this.extractedLetters[0]){
        up = null;
        break;
      }
      if(uid[0] === this.extractedLetters[i]){
        up = this.extractedLetters[i-1] + uid[1];
        break;
      }
    }

    let down;
    for(let i = 0; i < this.extractedLetters.length; i++){
      if(uid[0] === this.extractedLetters[this.extractedLetters.length-1]){
        down = null;
        break;
      }
      if(uid[0] === this.extractedLetters[i]){
        down = this.extractedLetters[i+1] + uid[1];
        break;
      }
    }

    let left;
    for(let i = 0; i < this.extractedLetters.length; i++){
      if(uid[1] === "1"){
        left = null;
        break;
      } else {
        left = uid[0] + (uid[1] - 1);
        break;
      }
    }

    let right;
    for(let i = 0; i < this.extractedLetters.length; i++){
      if(uid[1] === this.extractedNums[this.extractedNums.length-1]){
        right = null;
        break;
      } else {
        right = uid[0] + (parseInt(uid[1]) + 1);
        break;
      }
    }

    return {up,right,down,left}
  }

  toggleLights = (uid) => {

    let coordinates = this.lightCoordinates(uid);

  }



  render() {
    return(
      <div>
        <h1 onClick={() => console.log(this.lightCoordinates("b2"))}>Lights Out</h1>
        <table className="Board-grid">
          <tbody>
            <tr>
              {this.generateCells("a")}
            </tr>
            <tr>
              {this.generateCells("b")}
            </tr>
            <tr>
              {this.generateCells("c")}
            </tr>
            <tr>
              {this.generateCells("d")}
            </tr>
            <tr>
              {this.generateCells("e")}
            </tr>
          </tbody>
        </table>
        {this.extractLetters()}
      </div>
    )
  }
}


export default Board;
