import React, {Component} from "react";
import Cell from "./Cell";
import './Board.css';

class Board extends Component {
  state = {
    win: false,
    cellState: {}
  }

  cellList = new Set();
  extractedLetters = new Set();
  extractedNums = new Set();


  extractLetters = () => {                                /* THIS FUNCTION EXTRACTS THE LETTERS FROM THE CELLLIST ARRAY */

    this.cellList.forEach((cell) => {                     // Loop through the 'cellList',
      this.extractedLetters.add(cell[0]);                 // & add a letter to the 'extractedLetters' Set,
      this.extractedNums.add(cell[1]);                    // & add a number to the 'extractedNums' Set
    });

  }

  generateCoordinates = (uid) => {

    this.extractLetters();

    let extractedLetters = ["a","b","c","d","e"];
    let extractedNums = [...this.extractedNums];

    let up;
    for(let i = 0; i < extractedLetters.length; i++){
      if(uid[0] === extractedLetters[0]){
        up = null;
        break;
      }
      if(uid[0] === extractedLetters[i]){
        up = extractedLetters[i-1] + uid[1];
        break;
      }
    }

    let down;
    for(let i = 0; i < extractedLetters.length; i++){
      if(uid[0] === extractedLetters[extractedLetters.length-1]){
        down = null;
        break;
      }
      if(uid[0] === extractedLetters[i]){
        down = extractedLetters[i+1] + uid[1];
        break;
      }
    }

    let left;
    for(let i = 0; i < extractedLetters.length; i++){
      if(uid[1] === "1"){
        left = null;
        break;
      } else {
        left = uid[0] + (uid[1] - 1);
        break;
      }
    }

    let right;
    for(let i = 0; i < extractedLetters.length; i++){
      if(uid[1] === extractedNums[extractedNums.length-1]){
        right = null;
        break;
      } else {
        right = uid[0] + (parseInt(uid[1]) + 1);
        break;
      }
    }

    return {up,right,down,left}
  }

  generateCells = (char) => {
    return [...Array(5)].map((x,i) => {
      let id = char + (i + 1);
      this.cellList.add(id);
      return <Cell toggle={this.toggleCells} coords={this.generateCoordinates(id)} id={id} key={id} />
    });

  }

  toggleCells = (cell,coords) => {
    this.setState(st => {
      st.cellState[cell] = !st.cellState[cell]; // Actual Cell
      st.cellState[coords.up] = !st.cellState[coords.up];
      return st
    });
    console.log(this.coords)
  }

  componentDidMount = () => {
    this.setState((st) => {
      this.cellList.forEach((cell) => {
        let random = Math.floor(Math.random() * 2);
        st.cellState[cell] = (random ? true : false);
        return st
      });
    });
  }

  render() {
    return(
      <div>
        <h1 onClick={() => console.log(this.coords)}>Lights Out</h1>
        <table className="Board-grid">
          <tbody>
            <tr>
              {this.generateCells("a")}
            </tr>
            <tr>
              {this.generateCells("b")}
            </tr>
            <tr>
              {this.generateCells("c")}
            </tr>
            <tr>
              {this.generateCells("d")}
            </tr>
            <tr>
              {this.generateCells("e")}
            </tr>
          </tbody>
        </table>
      </div>
    )
  }
}


export default Board;
